import React, { Component } from 'react';
import './App.css';

class App extends Component {
  constructor(props) {
    super(props);
  }
  state = { 
    dado: 0
   }
  render() { 
    return ( <div className="App">
      <header className="App-header">
        <div>dado: {this.state.dado}</div>
        <button onClick={this.tirarDado()}></button>
      </header>
    </div> 
    );
  }

  tirarDado = ()=>{
    this.setState({
      dado: Number(Math.floor(Math.random()*6)+1)
    });
  }

}

export default App;
